mapper.insert:

SELECT NVL(MAX(site_num),0) + 1 FROM site


INSERT INTO site (
	site_num
	,site_nm
	,addr
	,phn_num
	,cont_amt
	,in_peo
	,st_dt
	,ex_com_dt
	,com_dt
	,etc
)
VALUES( 
	#{siteNum}
	,#{siteNm}
	,#{addr}
	,#{phnNum}
	,#{contAmt}
	,#{inPeo}
	,#{stDt}
	,#{exComDt}
	,#{comDt}
	,#{etc}
)


SELECT NVL(MAX(site_num), TO_CHAR(SYSDATE, 'yyyy') || '000') + 1 
FROM site
WHERE site_num LIKE TO_CHAR(SYSDATE, 'yyyy') || '%'


INSERT INTO site (
	site_num
	,site_nm
	,addr
	,phn_num
	,cont_amt
	,in_peo
	,st_dt
	,ex_com_dt
	,com_dt
	,etc
)
VALUES( 
	#{siteNum}
	,#{siteNm}
	,#{addr}
	,#{phnNum}
	,#{contAmt}
	,#{inPeo}
	,#{stDt}
	,#{exComDt}
	,#{comDt}
	,#{etc}
)

mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY site_num DESC) rnum ,site_num AS "siteNum" ,site_nm AS "siteNm" ,addr AS "addr" ,phn_num AS "phnNum" ,cont_amt AS "contAmt" ,in_peo AS "inPeo" ,TO_CHAR(st_dt, 'yyyy-mm-dd') AS "stDt" ,TO_CHAR(ex_com_dt, 'yyyy-mm-dd') AS "exComDt" ,TO_CHAR(com_dt, 'yyyy-mm-dd') AS "comDt" ,etc AS "etc" FROM site WHERE 1=1
AND site_nm LIKE '%' || #{keyword} || '%' OR addr LIKE '%' || #{keyword} || '%' OR etc LIKE '%' || #{keyword} || '%'
) WHERE rnum BETWEEN #{start} AND #{end}

mapper.update:

UPDATE site
SET 
    site_num  	= #{siteNum}
    ,site_nm    = #{siteNm}
    ,addr       = #{addr}
    ,phn_num    = #{phnNum}
    ,cont_amt   = #{contAmt}
    ,in_peo     = #{inPeo}
    ,st_dt      = #{stDt}
    ,ex_com_dt  = #{exComDt}
    ,com_dt     = #{comDt}
    ,etc        = #{etc}
WHERE site_num  = #{siteNum}
