mapper.insert:

SELECT NVL(MAX(mat_nm_cd),0) + 1 FROM site_mat


INSERT INTO site_mat (
	site_num
	,mat_nm_cd
	,pur_dt
	,cnt
	,pur_pri
)
VALUES( 
	#{siteNum}
	,#{matNmCd}
	,#{purDt}
	,#{cnt}
	,#{purPri}
)

mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY a.mat_nm_cd DESC) rnum ,a.site_num AS "siteNum" ,a.mat_nm_cd AS "matNmCd" ,TO_CHAR(a.pur_dt, 'yyyy-mm-dd') AS "purDt" ,a.cnt AS "cnt" ,a.pur_pri AS "purPri" ,b.item_nm AS "itemNm" ,c.site_nm AS "siteNm" FROM site_mat a, con_mat_info b, site c WHERE a.mat_nm_cd = b.mat_nm_cd AND a.site_num = c.site_num
AND a.mat_nm_cd LIKE '%' || #{keyword} || '%' OR a.site_num LIKE '%' || #{keyword} || '%'
) WHERE rnum BETWEEN #{start} AND #{end}

mapper.update:

UPDATE site_mat
SET 
    site_num 	 = #{siteNum} 
    ,pur_dt      = #{purDt}   
    ,cnt         = #{cnt}     
    ,pur_pri     = #{purPri}  
WHERE mat_nm_cd  = #{matNmCd}
