mapper.insert:

SELECT NVL(MAX(wk_num),0) + 1 FROM wk


INSERT INTO wk (
	wk_num
	,emp_num
	,site_num
	,in_dt
	,out_dt
)
VALUES(
	#{wkNum}
	,#{empNum}
	,#{siteNum}
	,#{inDt}
	,#{outDt}
)

mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY wk_num DESC) rnum ,wk_num AS "wkNum" ,emp_num AS "empNum" ,site_num AS "siteNum" ,TO_CHAR(in_dt, 'yyyy-mm-dd') AS "inDt" ,TO_CHAR(out_dt, 'yyyy-mm-dd') AS "outDt" FROM wk WHERE 1=1
AND emp_num LIKE '%' || #{keyword} || '%' OR site_num LIKE '%' || #{keyword} || '%'
) WHERE rnum BETWEEN #{start} AND #{end}

mapper.update:

UPDATE wk
SET 
    wk_num    	 = #{wkNum} 
    ,emp_num     = #{empNum}   
    ,site_num    = #{siteNum}   
    ,in_dt       = #{inDt}     
    ,out_dt      = #{outDt}  
WHERE wk_num  	 = #{wkNum}
