mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY emp_num DESC) rnum , emp_num AS "empNum" , emp_nm AS "empNm" , zip_code AS "zipCode" , addr1 AS "addr1" , addr2 AS "addr2" , phn_num AS "phnNum" , pos AS "pos" , dept_nm AS "deptNm" FROM emp WHERE 1 = 1
AND emp_nm LIKE '%' || #{keyword} || '%' OR pos LIKE '%' || #{keyword} || '%' OR dept_nm LIKE '%' || #{keyword} || '%'
) WHERE rnum BETWEEN #{start} AND #{end}

mapper.update:

UPDATE emp
SET 
    emp_nm  	= #{empNm}  
    , zip_code  = #{zipCode}
    , addr1     = #{addr1}  
    , addr2     = #{addr2}  
    , phn_num   = #{phnNum} 
    , pos       = #{pos}    
    , dept_nm   = #{deptNm} 
WHERE emp_num = #{empNum}
